mapper.select:
select u.*,r.roleName from smbms_user u, smbms_role r
         where userName like concat('%',#{userName},'%')
            and userRole=#{userRole} and u.userRole=r.id;

select u.*,r.roleName from smbms_user u, smbms_role r
        where userName like concat('%',#{userName},'%')
            and userRole=#{userRole} and u.userRole=r.id;
